var opts = {
  pattern: /\/show\/1\/([^\/]+)\//,
  name: '$.title',
  author: '$.authors'
};

//删除糢糊匹配的搜索结果，然后按书名长度排序。依赖于baseUrl、result变量。
//return Array
//opts Object: 参数，包含以下key：
//target List<Object>(result): 要过滤列表。
//pattern RegExp: 匹配searchKey的正则表达式。
//name String: 书名规则。
//author String: 作者规则。
//charset String('UTF-8'): 编码方式。
//sorting Boolean(true): 是否重新排序。

function searchFilter(opts){
  result = opts.target === undefined ? result : opts.target;
  opts.charset = opts.charset === undefined ? 'UTF-8' : opts.charset;
  opts.sorting = opts.sorting === undefined ? true : !!opts.sorting;
  function getText(ele, rule){
    java.setContent(ele);
    return String(java.getString(rule));
  }
  try {
    var kw = (baseUrl.match(opts.pattern) || ['',''])[1];
    kw = Packages.java.net.URLDecoder.decode(kw, opts.charset);
    result = result.toArray().filter(e => getText(e, opts.name).includes(kw) || getText(e, opts.author).includes(kw));
    return opts.sorting ? result.sort((a, b) => getText(a, opts.name).length - getText(b, opts.name).length) : result;
  } catch(e) {
    return result;
  }
}

searchFilter(opts);
</js>

