.search_box<js>
var bookCount=2,searchList=[];
importClass(org.jsoup.Jsoup);
function aa(b){
return doc.select("[property="+b+"]").attr("content")
}
for(var i=1;i<=bookCount;i++){
var url=result.select(".search_list:eq("+i+")>a").attr("href");
var doc=Jsoup.parse(java.ajax('https://xsbiquge.net'+url));
searchList.push({
name:aa("og:novel:book_name"),
author:aa("og:novel:author"),
tag:aa("og:novel:category")+','+doc.select(".tab1 .p5").text()+','+doc.select(".tab1 .p6").text(),
cover:aa("og:image"),
intro:doc.select(".jj .p2").text(),
last:aa("og:novel:latest_chapter_name"),
url:url
})
}
